echo "
/* Info Grid (Updates & Related) */
.two-column-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 3rem;
    margin-top: 2rem;
    border-top: 1px solid #eee;
    padding-top: 2rem;
}

@media (max-width: 768px) {
    .two-column-grid {
        grid-template-columns: 1fr;
    }
}

.info-column h3 {
    font-size: 1.8rem;
    margin-bottom: 1rem;
    color: var(--text-color);
    font-weight: normal;
}

.info-list {
    list-style-type: disc;
    padding-left: 1.5rem;
    color: #333;
}

.info-list li {
    margin-bottom: 0.8rem;
    line-height: 1.5;
}

.info-list a {
    color: var(--accent-color);
    text-decoration: none;
}

.info-list a:hover {
    text-decoration: underline;
}

.badge {
    background-color: #28a745;
    color: white;
    padding: 0.1rem 0.4rem;
    border-radius: 3px;
    font-size: 0.8rem;
    font-weight: bold;
    margin-right: 0.3rem;
}" >> /home/twan/Downloads/quic-vn.github.io/style.css
